mapper.select:
select img.image_name, p.product_no, p.product_name, p.product_price, p.product_state, s.seller_company, s.seller_ceo, j.zzim from products p, product_img img, user_seller s, product_category c, (select products.product_no, count(product_zzim.product_no) zzim from products left outer join product_zzim on products.product_no = product_zzim.product_no group by products.product_no) j where p.product_no = img.product_no and j.product_no = p.product_no and p.seller_no = s.seller_no and p.category_no = c.category_no
and ${whereField} = #{categoryNo}
and thumbnail = '1'
order by product_state, ${orderField} ${sortOrder}
order by product_state, p.product_name

select image_name
from product_img
where product_no=#{productNo}

mapper.update:
update products 
set product_cnt = product_cnt + 1 
where product_no=#{productNo}

mapper.select:
select count(zzim_no) 
from product_zzim 
where user_no=#{userNo} and product_no=#{productNo}
